import React, { Component } from 'react';
import CardList from './CardList.js';
import { candies } from './candies';
import SearchBox from './SearchBox';

class App extends Component {
    constructor() {
        super()
        this.state = {
            candies: candies,
            searchfield: ''
        }
    }

onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value })
    const filteredCandies = this.state.candies.filter(candy => {
        return candy.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    })

}

    render() {
        return (
            <div className='tc dib br3 pa3 ma2 bw2'>
                <h1>Museum of Candy</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <CardList candies={this.state.candies} />
            </div>
        );
    }

}

export default App;
